import React, { useState } from 'react';
import axios from 'axios';

const AddTenant = () => {
    const [name, setName] = useState('');
    const [apartment, setApartment] = useState('');
    const [rent, setRent] = useState('');
    const [contractStart, setContractStart] = useState('');
    const [contractEnd, setContractEnd] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();

        const newTenant = {
            name,
            apartment,
            rent,
            contract_start: contractStart,
            contract_end: contractEnd
        };

        axios.post('http://localhost:3001/tenants', newTenant)
            .then(response => {
                alert('Locataire ajouté avec succès');
                // Reset form fields
                setName('');
                setApartment('');
                setRent('');
                setContractStart('');
                setContractEnd('');
            })
            .catch(error => {
                console.error('Erreur lors de l\'ajout du locataire:', error);
            });
    };

    return (
        <div>
            <h2>Ajouter un locataire</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Nom:</label>
                    <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
                </div>
                <div>
                    <label>Appartement:</label>
                    <input type="text" value={apartment} onChange={(e) => setApartment(e.target.value)} required />
                </div>
                <div>
                    <label>Loyer:</label>
                    <input type="number" value={rent} onChange={(e) => setRent(e.target.value)} required />
                </div>
                <div>
                    <label>Date de début de contrat:</label>
                    <input type="date" value={contractStart} onChange={(e) => setContractStart(e.target.value)} required />
                </div>
                <div>
                    <label>Date de fin de contrat:</label>
                    <input type="date" value={contractEnd} onChange={(e) => setContractEnd(e.target.value)} required />
                </div>
                <button type="submit">Ajouter</button>
            </form>
        </div>
    );
};

export default AddTenant;
