import React, { useState } from 'react';
import axios from 'axios';

const AddTenant = () => {
    const [name, setName] = useState('');
    const [apartment, setApartment] = useState('');
    const [rent, setRent] = useState('');
    const [contractStart, setContractStart] = useState('');
    const [contractEnd, setContractEnd] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        const newTenant = {
            name,
            apartment,
            rent,
            contract_start: contractStart,
            contract_end: contractEnd
        };

        try {
            await axios.post('http://localhost:3001/tenants', newTenant); // Ajoutez le port 3001 ici
            console.log('Locataire ajouté avec succès');
            // Réinitialiser les champs après l'ajout
            setName('');
            setApartment('');
            setRent('');
            setContractStart('');
            setContractEnd('');
        } catch (error) {
            console.error('Erreur lors de l\'ajout du locataire', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <div>
                <label>Nom :</label>
                <input
                    type="text"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                />
            </div>
            <div>
                <label>Appartement :</label>
                <input
                    type="text"
                    value={apartment}
                    onChange={(e) => setApartment(e.target.value)}
                />
            </div>
            <div>
                <label>Loyer :</label>
                <input
                    type="number"
                    value={rent}
                    onChange={(e) => setRent(e.target.value)}
                />
            </div>
            <div>
                <label>Début du contrat :</label>
                <input
                    type="date"
                    value={contractStart}
                    onChange={(e) => setContractStart(e.target.value)}
                />
            </div>
            <div>
                <label>Fin du contrat :</label>
                <input
                    type="date"
                    value={contractEnd}
                    onChange={(e) => setContractEnd(e.target.value)}
                />
            </div>
            <button type="submit">Ajouter le locataire</button>
        </form>
    );
};

export default AddTenant;
