import React, { useState } from 'react';
import { addTenant } from '../api';

const AddTenant = () => {
    const [tenantInfo, setTenantInfo] = useState({
        firstName: '',
        lastName: '',
        apartment: '',
        address: '',
        startDate: '',
        endDate: ''
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setTenantInfo({ ...tenantInfo, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const newTenant = await addTenant(tenantInfo);
            console.log('Nouveau locataire ajouté :', newTenant);
            // Réinitialiser le formulaire ou afficher un message de succès
        } catch (error) {
            console.error('Erreur lors de l\'ajout du locataire :', error);
            // Afficher une erreur à l'utilisateur
        }
    };

    return (
        <div className="add-tenant-container">
            <h2>Ajouter un nouveau locataire</h2>
            <form onSubmit={handleSubmit}>
                <input type="text" name="firstName" placeholder="Prénom" value={tenantInfo.firstName} onChange={handleInputChange} />
                <input type="text" name="lastName" placeholder="Nom" value={tenantInfo.lastName} onChange={handleInputChange} />
                <input type="text" name="apartment" placeholder="Appartement" value={tenantInfo.apartment} onChange={handleInputChange} />
                <input type="text" name="address" placeholder="Adresse" value={tenantInfo.address} onChange={handleInputChange} />
                <input type="date" name="startDate" placeholder="Date de début de contrat" value={tenantInfo.startDate} onChange={handleInputChange} />
                <input type="date" name="endDate" placeholder="Date de fin de contrat" value={tenantInfo.endDate} onChange={handleInputChange} />
                <button type="submit">Ajouter</button>
            </form>
        </div>
    );
};

export default AddTenant;
